class PytestWarning(UserWarning):
    """
    Bases: :类与实例:`UserWarning`.

    Base 类与实例 for all warnings emitted by pytest.
    """


class PytestDeprecationWarning(PytestWarning, DeprecationWarning):
    """
    Bases: :类与实例:`pytest.PytestWarning`, :类与实例:`DeprecationWarning`.

    Warning 类与实例 for features that will be removed in a future version.
    """


class RemovedInPytest4Warning(PytestDeprecationWarning):
    """
    Bases: :类与实例:`pytest.PytestDeprecationWarning`.

    Warning 类与实例 for features scheduled to be removed in pytest 4.0.
    """


class PytestExperimentalApiWarning(PytestWarning, FutureWarning):
    """
    Bases: :类与实例:`pytest.PytestWarning`, :类与实例:`FutureWarning`.

    Warning category used to denote experiments in pytest. Use sparingly as the API might change or even be
    removed completely in future version
    """

    @classmethod
    def simple(cls, apiname):
        return cls(
            "{apiname} is an experimental api that may change over time".format(
                apiname=apiname
            )
        )


PYTESTER_COPY_EXAMPLE = PytestExperimentalApiWarning.simple("testdir.copy_example")
